-- ----------------------------
-- Table structure for city
-- ----------------------------
DROP TABLE IF EXISTS `city`;
CREATE TABLE `city` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `city_Name` varchar(255) DEFAULT NULL,
  `city_Introduce` varchar(255) DEFAULT NULL COMMENT '简介',
  `city_photo` varchar(100) DEFAULT NULL COMMENT '图片',
  `city_Click_Count` int(10) DEFAULT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of city
-- ----------------------------
INSERT INTO `city` VALUES ('1', '北京', '北京市，简称“京”，是中华人民共和国省级行政区、首都、直辖市、国家中心城市、超大城市，全国政治中心、文化中心、国际交往中心、科技创新中心，是世界著名古都和现代化国际城市， [1]  是中国共产党中央委员会、中华人民共和国中央人民政府和全国人民代表大会常务委员会的办公所在地。', 'img/citys/beijing.jpg', '91');
INSERT INTO `city` VALUES ('2', '上海', '上海市，简称“沪”，是中华人民共和国省级行政区。上海是中国共产党诞生地、国家历史文化名城，中国经济、金融、贸易、航运、科技创新中心。 [1]  上海位于中国华东地区，界于东经120°52′-122°12′，北纬30°40′-31°53′之间，地处长江入海口，东隔东中国海与日本九州岛相望，南濒杭州湾，北、西与江苏、浙江两省相接，上海市总面积6340.5平方千米。 [2-4] ', 'img/citys/shanghai.jpeg', '32');
INSERT INTO `city` VALUES ('3', '长沙', '长沙，湖南省省会，是长江中游地区重要的中心城市 [1]  ，全国“两型社会”综合配套改革试验区、中国重要的粮食生产基地，长江中游城市群和长江经济带重要的节点城市。 [2-3]  东邻江西省宜春、萍乡，西连娄底、益阳，南接株洲、湘潭，北靠岳阳。长沙是综合交通枢纽，京广高铁、沪昆高铁、渝厦高铁在此交汇。', 'img/citys/changsha.jpg', '10');
INSERT INTO `city` VALUES ('4', '广州', '广州，简称“穗”，别称羊城、花城，是广东省省会、副省级市、国家中心城市、超大城市，国务院批复确定的中国重要的中心城市、国际商贸中心和综合交通枢纽 [1]  。截至2018年，全市下辖11个区，总面积7434平方千米，建成区面积1249.11平方千米，常住人口1490.44万人，城镇人口1287.44万人，城镇化率86.38%。 [2] ', 'img/citys/gz.jpeg', '452');
INSERT INTO `city` VALUES ('5', '长春', '长春，简称“长”，别称春城，古称喜都、茶啊冲、黄龙府，是吉林省省会、副省级市、东北亚经济圈中心城市，国务院批复确定的中国东北地区中心城市之一和重要的工业基地 [1]  。截至2018年，全市下辖7个区、1个县、代管2个县级市，总面积20565平方千米，建成区面积519.04平方千米，常住人口751.3万人，城镇人口441.5万人，城镇化率58.8%。 [2-3] ', 'img/citys/changchun.jpg', '2');
INSERT INTO `city` VALUES ('6', '厦门', '厦门，隶属于福建省，别称鹭岛， 简称厦、鹭， 副省级城市、经济特区 [1]  ，东南沿海重要的中心城市. [2]  厦门位于福建省东南端，东与大小金门岛、南与龙海市、漳州招商局经济技术开发区隔海相望，陆地与南安市、安溪县、长泰县、漳州台商投资区接壤。 [3]  厦门由厦门半岛、离岛鼓浪屿、西岸海沧半岛、北岸集美半岛、东岸翔安半岛、大嶝岛、小嶝岛、内陆同安、九龙江等组成，陆地面积1699.39平方公里，海域面积390多平方公里。厦门通行闽南语，是闽南地区的主要城市，与漳州、泉州同为闽南地区组成部分。', 'img/citys/xiamen.jpg', '19');
INSERT INTO `city` VALUES ('7', '岳阳', '岳阳，古称“巴陵”、又名“岳州”，湖南省地级市。第二大经济体，长江中游城市群重要成员，湖南省域副中心城市。建城始于公元前505年，是一座有着2500多年悠久历史的文化名城。位于江南洞庭湖之滨，依长江、纳三湘四水，江湖交汇，不仅是中国南北东西交通要道、国务院首批沿江开放之重地，且是长江中游重要的区域中心城市、湖南首位门户城市。 [1] ', 'img/citys/yueyang.jpg', '110');
INSERT INTO `city` VALUES ('8', '云南', '云南省，简称“云”或“滇”，是中华人民共和国省级行政区，省会昆明。位于中国西南边陲，界于北纬21°8′-29°15′，东经97°31′-106°11′之间，东部与贵州、广西为邻，北部与四川相连，西北部紧依西藏，西部与缅甸接壤，南部和老挝、越南毗邻，云南省总面积39.4万平方千米。 [1] ', 'img/citys/yunnan.jpeg', '134');
INSERT INTO `city` VALUES ('9', '海南', '海南省，简称“琼”，是中华人民共和国省级行政区，省会海口。海南省是中国的经济特区、自由贸易试验区（港）。位于中国华南地区，北以琼州海峡与广东划界，西临北部湾与广西、越南相对，东濒南海与台湾对望， 东南和南部在南海与菲律宾、文莱、马来西亚为邻，是中国最南端的省级行政区。海南省海南岛总面积3.39万平方千米，海域面积约200万平方千米。 [1] \r\n', 'img/citys/hainan.jpeg', '148');
INSERT INTO `city` VALUES ('10', '西安', '西安，古称长安、镐京，是陕西省会、副省级市、关中平原城市群核心城市，曾是古丝绸之路的起点 [1]  ，是“一带一路”核心区、中国西部地区重要的中心城市，是国家重要的科研、教育、工业基地 [2-6]  。联合国教科文组织于1981年确定的“世界历史名城” [2]  。地处关中平原中部，北濒渭河，南依秦岭，八水润长安。下辖11区2县并代管西咸新区，总面积10752平方公里，2018年末常住人口1000.37万人，常住人口城镇化率74.01%。全年地区生产总值8349.86亿元 [7]  ', 'img/citys/xian.jpeg', '462');

-- ----------------------------
-- Table structure for tour
-- ----------------------------
DROP TABLE IF EXISTS `tour`;
CREATE TABLE `tour` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `tour_num` varchar(50) DEFAULT NULL COMMENT ' 产品编号 ',
  `tour_name` varchar(200) DEFAULT NULL COMMENT '	产品名称(路线名称)',
  `city_name` varchar(50) DEFAULT NULL COMMENT '	出发城市',
  `departure_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '	出发时间 ',
  `tour_price` double(8,2) DEFAULT NULL COMMENT '产品价格 ',
  `tour_desc` varchar(500) DEFAULT NULL COMMENT '	产品描述',
  `tour_status` int(11) DEFAULT NULL COMMENT '状态 (0开启 1关闭)',
  `tour_photo` varchar(100) DEFAULT NULL,
  `tour_head` int(10) DEFAULT NULL COMMENT '热度',
  `tour_comment` int(10) DEFAULT NULL COMMENT '评论',
  `tour_score` double(3,1) DEFAULT NULL COMMENT '评分',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=14 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Records of tour
-- ----------------------------
INSERT INTO `tour` VALUES ('8', '10001', '【网红纯玩旅拍】+云南大理丽江泸沽湖7天6晚跟团游（海量好评真纯玩+动车往返+吉普车洱海旅拍+丽水金沙+泸沽湖环湖+三塔+玉龙雪山+拉市海）', '大理', '2019-08-29 15:22:47', '2620.00', '昆明长水国际机场到A出口国内到达大厅左转30米处3号门旁（不出3号门），至“爱旅行”白色接机柜台，到柜台后对您的接待信息进行核对(包括参加旅游产品名称、姓名、参团人数、所乘坐航班)，由旅行社工作人员负责送您去所下榻酒店休息。到酒店入住，办理正规的旅游手续，签定旅游合同。\r\n若抵达时间尚早， 各位团友可尽情品尝当地风味小吃，也可自行前往【滇池、翠湖公园、海埂公园、金马碧鸡坊、大观楼、云南陆军讲武堂旧址】 ，费用自理。以下为推荐景点，不在行程包含之内。\r\n温馨提示\r\n1、报名时请留下您在旅游期间使用的手机号码，方便导游用短信与您联络；\r\n2、明天出发时间较早，路途行车时间长，请提前入睡休息好，酒店均含独立卫生间，提供热水，电视机及早餐，按时起床早餐后带好行李到酒店大堂集合即可。温馨提示：以下景点飞机时间是早航班可自行前往，费用自理，敬请谅解。', '0', 'img/tour/dl.jpeg', '10', '269', '6.1');
INSERT INTO `tour` VALUES ('9', '10002', '全国高铁往返上海+南京中山陵+杭州+乌镇+南浔古镇+瘦西湖+鼋头渚6天5晚跟团游（赠船游苏州古运河+赠杭州宋城千古情表演+赠上海夜景套票）', '上海', '2019-08-29 15:22:51', '2140.00', '★航班信息：请根据客服提供的航班信息，自行搭乘飞机前往上海！安排免费接机，接机后乘车前往酒店办理入住。\r\n★温馨提示：\r\n1、按照国际惯例，酒店的入住时间为下午14：00以后，若您的抵达时间较早，建议您将行李寄存前台。\r\n2、接机为拼车接送，可能出现等待的情况（具体以接机人员告知为准），请耐心等待，谢谢您的理解。\r\n3、导游晚上会与客人联系通知第二天的接人时间、所以请保持落地后手机随时畅通！\r\n4、入住酒店时，酒店均需收取一定押金，按照酒店不同标准，每间客房约100-300元不等，需要游客在酒店前台自行支付。若有损坏酒店物品、丢失房卡等，需自行赔偿酒店损失。若无物品损坏及其他消费等则退房时凭押金条退回所交全部押金。', '0', 'img/tour/sh.jpeg', '12', '312', '8.8');
INSERT INTO `tour` VALUES ('10', '10003', '湘见芙蓉镇·长沙+韶山+张家界森林公园+宝峰湖+天门山+凤凰6日游(品4大美食/赠送魅力湘西晚会）', '长沙', '2019-08-29 08:39:43', '1360.00', '出发抵达长沙，我们提供24h免费接站服务，专人接送前往指定酒店，办理入住。如果时间尚早，您可自行畅游长沙！\r\n自由活动推荐：您可自行前往【岳麓书院】、【爱晚亭】、【岳麓山】游玩，晚上可到坡子街品尝长沙特色美食（长沙臭豆腐、口味虾、口味蛇、糖油粑粑等等各种小吃）。\r\n【温馨提示】：如您到达酒店后要外出活动，请保管好贵重物品，注意安全，晚上尽量早点回酒店休息！\r\n【温馨提示】：长沙指定酒店若遇大型接待任务，无法安排入住，则安排更换其他豪华酒店。\r\n【备选酒店】：康莱、金领、圣卡洛、泰悦年华、楠庭、马栏坡花园、石长华天或同级同级其他酒店', '0', 'img/tour/cs.jpeg', '23', '416', '9.2');
INSERT INTO `tour` VALUES ('11', '10004', '高铁往返北京全景游5天4晚（一价全含0购物0自费景点+餐餐特色+4晚连锁酒店连住+专车专导+无线讲解耳麦+升旗仪式+恭王府）', '北京', '2019-08-29 08:38:51', '2345.00', '全国各出发地自行取票上车抵达北京接送专员接至酒店入住 师傅会在您抵京前一天联系您，抵达后我社特派专职人员接站,并为您安排入住酒店。如果您抵京后时间充裕，我们建议您自行前往王府井大街、西单，东单，国贸，三里屯等商业街区逛逛，因为那里是现代北京的代表。（乘车路线可拨打12580咨询）\r\n温馨提示： \r\n1、按照国际惯例，酒店的入住时间为下午14：00以后，若您的抵达时间较早，建议您将行李寄存前台。入住酒店时，酒店均需收取一定押金（按照酒店不同标准，每人100-300元不等），需要游客在酒店前台自行支付。若有损坏酒店物品、丢失房卡等，需自行赔偿酒店损失。若无物品损坏及其他消费等则退房时凭押金条退回所交全部押金。\r\n2、关于第二天的旅游行程， 客人抵达当天晚上21：00前会收到导游电话（短信），通知第二天旅游事宜，敬请保持手机畅通。以上时间仅供参考，具体以当天实际安排为准，景点的先后顺序以当天实际安排为准； 第一天和最后一天由于接送站（机）时间不同，在接站过程中可能有等候情况，我们将会统一安排接送，出现等候情况，敬请谅解！', '0', 'img/tour/bj.jpeg', '54', '211', '8.3');
INSERT INTO `tour` VALUES ('12', '10005', '【玩转鼓浪屿】动车厦门4日 0购物0自费不早起 浪漫鼓浪屿+骑行七彩环岛路+南普陀（超值赠送/体验厦门慢生活/虾兵蟹将海鲜餐）', '厦门', '2019-08-29 08:39:40', '2740.00', '★【品质保证】★ \r\n\r\n①全程0购物0自费，不早起，有充裕的时间看景，让旅行回到大自然\r\n\r\n②不随便更改行程所含景点，严格按形成标准执行，无偷梁换柱的风险（不可抗力因素除外）。\r\n\r\n★【特别赠送】★ \r\n\r\n①虾兵蟹将海鲜餐\r\n\r\n②鼓浪屿全程无线耳麦免费使用\r\n\r\n③赠送环岛路观光自行车骑行\r\n\r\n④赠送鼓浪屿盖章本和手绘地图一份\r\n\r\n⑤免费获赠音乐艺人原创专辑，鼓浪屿之波光碟一张\r\n\r\n★【赔付体系】★ \r\n\r\n①凡因未按产品描述规范操作，导游强制消费、进店产生的投诉，一经查实，赔付1000元/人（游客主动自行、自愿参加景点除外）。\r\n\r\n②凡出现漏接等情况，一经查实，赔付100元/人\r\n\r\n③凡未按照十项游客服务标准执行的，一经查实，赔付50/人\r\n\r\n★【温馨住宿】★ \r\n\r\n好的睡眠让您旅途更舒心，精心挑选舒适酒店，不住小旅馆，拒绝脏乱差。\r\n\r\n★【出行无忧】★\r\n\r\n①铁定成团，一人也发团。\r\n\r\n②专属客服24小时电话畅通，导游会在出行前一天晚上21点（淡季）前，旺季每晚22点前告知游客来厦门相关注意事项。', '0', 'img/tour/xm.jpeg', '12', '117', '7.1');
INSERT INTO `tour` VALUES ('13', '10006', '纯玩-高铁往返-杭州西湖雷峰塔+苏州拙政园+无锡三国城水浒城+南京中山陵4日3晚游', '南京', '2019-08-29 08:39:33', '2264.00', '产品特色：\r\n\r\n秦淮风光，淮扬特色，园林文化，水乡风情，浪漫与享受尽在流连往返中。\r\n\r\n全程无自费，含价值720元的精华夜景：苏州古运河游船、宋城千古情表演、上海夜景。\r\n品尝老扬州特色早茶，欣赏扬州文化表演-【千秋粉黛】。\r\n全程豪华酒店（准四不挂牌），升级一晚五钻星级酒店（五星不挂牌）。\r\n江南二大水乡：水乡【周庄】、原汁原味的水乡—【乌镇.东栅】各有各的历史，各有各的风情。\r\n最经典二大园林：历史上实权和土豪的情趣见证。盐商私家园林-扬州【个园】和苏州代表性园林--【耦园】\r\n两大评弹：扬州小调--【千秋粉黛】、苏州小调--【苏州评弹】\r\n网红打卡地：带上行头背上背篓万亩茶园拍照留念\r\n漫步杭州【西湖】\r\n纯玩无购物，尽情享受旅途乐趣！', '0', 'img/tour/nj.jpeg', '22', '132', '7.6');

-- ----------------------------
-- Table structure for tourorder
-- ----------------------------
DROP TABLE IF EXISTS `tourorder`;
CREATE TABLE `tourorder` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `tour_order_num` varchar(50) DEFAULT NULL COMMENT '	订单编号',
  `tour_order_time` varchar(20) NOT NULL COMMENT '下单时间',
  `people_count` int(11) DEFAULT NULL COMMENT '出行人数',
  `tour_order_status` int(11) DEFAULT NULL COMMENT '	订单状态(0未支付 1已支付)',
  `tour_Id` int(11) DEFAULT NULL COMMENT '	跟团游商品表ID(外键)',
  `userInfo_Id` int(11) DEFAULT NULL COMMENT '下单用户ID(外键)',
  `userInfo_name` varchar(10) DEFAULT NULL COMMENT '下单用户姓名',
  `tour_order_price` double(8,2) DEFAULT NULL COMMENT '总价',
  PRIMARY KEY (`Id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=15 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

