-- ----------------------------
-- Table structure for view
-- ----------------------------
DROP TABLE IF EXISTS `view`;
CREATE TABLE `view` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `view_name` varchar(30) DEFAULT NULL,
  `view_Introduce` varchar(255) DEFAULT NULL COMMENT '--简介',
  `view_Image` varchar(255) DEFAULT NULL,
  `city_Id` int(11) DEFAULT NULL COMMENT '外键,城市ID ,city ',
  `view_Click_Count` int(10) DEFAULT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=MyISAM AUTO_INCREMENT=58 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of view
-- ----------------------------
INSERT INTO `view` VALUES ('2', '故宫', '北京故宫是中国明清两代的皇家宫殿，旧称紫禁城，位于北京中轴线的中心，是中国古代宫廷建筑之精华。北京故宫以三大殿为中心，占地面积72万平方米，建筑面积约15万平方米，有大小宫殿七十多座，房屋九千余间。是世界上现存规模最大、保存最为完整的木质结构古建筑之一。', 'img/views/lalala.jpg', '1', '8');
INSERT INTO `view` VALUES ('19', '长城', '长城修筑的历史可上溯到西周时期，发生在首都镐京（今陕西西安）的著名的典故“烽火戏诸侯”就源于此。春秋战国时期列国争霸，互相防守，长城修筑进入第一个高潮，但此时修筑的长度都比较短。秦灭六国统一天下后，秦始皇连接和修缮战国长城，始有万里长城之称 [1]  。明朝是最后一个大修长城的朝代，今天人们所看到的长城多是此时修筑。 [2] ', 'img/views/changcheng.jpg', '1', '13');
INSERT INTO `view` VALUES ('18', '天安门', '天安门是明清两代北京皇城的正门，始建于明朝永乐十五年（1417年），最初名“承天门”，寓“承天启运、受命于天”之意。设计者为明代御用建筑匠师蒯祥。清朝顺治八年（1651年）更名为天安门。由城台和城楼两部分组成，有汉白玉石的须弥座，总高34.7米。天安门城楼长66米、宽37米。城台下有券门五阙，中间的券门最大，位于北京皇城中轴线上，过去只有皇帝才可以由此出入。正中门洞上方悬挂着毛泽东画像，两边分别是“中华人民共和国万岁”和“世界人民大团结万岁”的大幅标语。 [1] ', 'img/views/tiananmen.jpg', '1', '15');
INSERT INTO `view` VALUES ('20', '颐和园', '颐和园，中国清朝时期皇家园林，前身为清漪园，坐落在北京西郊，距城区15公里，占地约290公顷，与圆明园毗邻。它是以昆明湖、万寿山为基址，以杭州西湖为蓝本，汲取江南园林的设计手法而建成的一座大型山水园林，也是保存最完整的一座皇家行宫御苑，被誉为“皇家园林博物馆”，也是国家重点旅游景点。', 'img/views/yiheyuan.jpg', '1', '9');
INSERT INTO `view` VALUES ('21', '东方明珠', '东方明珠广播电视塔（The Oriental Pearl Radio & TV Tower）是上海的标志性文化景观之一，位于浦东新区陆家嘴，塔高约468米。该建筑于1991年7月兴建，1995年5月投入使用，承担上海6套无线电视发射业务，地区覆盖半径80公里。 [1-3] \r\n东方明珠广播电视塔是国家首批AAAAA级旅游景区。塔内有太空舱、旋转餐厅、上海城市历史发展陈列馆等景观和设施，1995年被列入上海十大新景观之一。 [4-5] ', 'img/views/dongfangmingzhu.jpg', '2', '22');
INSERT INTO `view` VALUES ('22', '外滩', '外滩矗立着52幢风格迥异的古典复兴大楼，素有外滩万国建筑博览群之称，是中国近现代重要史迹及代表性建筑，上海的地标之一。1996年11月，国务院将其列入第四批全国重点文物保护单位。与外滩隔江相对的浦东陆家嘴，有上海标志性建筑东方明珠、金茂大厦、上海中心大厦、上海环球金融中心等，成为中国改革开放的象征和上海现代化建设的缩影。', 'img/views/waitan.jpg', '2', '12');
INSERT INTO `view` VALUES ('23', '和平饭店', '和平饭店是上海市地标性建筑，位于上海黄浦区南京东路20号，是上海近代建筑史上第一幢现代派建筑，拥有最具特色的九国式特色套房及众多别具特色的餐厅、宴会厅、多功能厅、酒吧、屋顶观光花园等。\r\n和平饭店是中国首家世界著名饭店，2005年的营业额达到1.88亿元，创利6668万元，接待宾客101468人次。', 'img/views/hepingfandian.jpg', '2', '14');
INSERT INTO `view` VALUES ('24', '上海美术馆', '上海美术馆是一所公益性社会文化事业机构。原址坐落于繁华的南京西路，背靠人民广场与上海博物馆、上海大剧院、上海城市规划展示馆、人民公园毗邻，是一座功能健全、设施先进、在国内外具有一定影响的近现代美术博物馆。2012年10月1日以后，上海美术馆将迁入原世博园的中国国家馆，并更名为中华艺术宫和上海当代艺术博物馆。\r\n', 'img/views/meishuguan.jpg', '2', '14');
INSERT INTO `view` VALUES ('25', '岳麓山', '岳麓山风景区位于湖南省长沙市岳麓区，海拔300.8米，占地面积35.20平方公里，是南岳衡山72峰的最后一峰，位于橘子洲旅游景区内，为城市山岳型风景名胜区，是中国四大赏枫胜地之一。\r\n岳麓山位于首批国家历史文化名城长沙市湘江西岸，依江面市，现有岳麓山、橘子洲、岳麓书院、新民学会四个核心景区，为世界罕见的集“山、水、洲、城”于一体的国家AAAAA级旅游景区、国家重点风景名胜区、湖湘文化传播基地和爱国主义教育的示范基地。', 'img/views/yuelushan.jpg', '3', '43');
INSERT INTO `view` VALUES ('26', '橘子洲', '橘子洲风景区位于湖南省长沙市市区对面的湘江江心，是湘江中最大的名洲，面积有91.4公顷。由南至北，横贯江心，西望岳麓山，东临长沙城，四面环水，绵延十多里，狭处横约40米，宽处横约140米，形状是一个长岛，为国家重点风景名胜区。\r\n橘子洲风景区开发建设结构规划为“一轴四区”，四区为核心景区、旅游配套服务区、沙滩公园、水上运动区。', 'img/views/juzizhou.jpg', '3', '126');
INSERT INTO `view` VALUES ('27', '马王堆汉墓', '马王堆汉墓是西汉初期长沙王丞相利苍及其家属的墓葬，位于湖南省长沙市。\r\n1972-1974年，考古工作者先后发掘了3座西汉时期墓葬。墓葬的结构宏伟复杂，椁室构筑在墓坑底部，由三椁、三棺及垫木所组成。木棺四周及其上部填有木炭，木炭外又用白膏泥填塞封固。出土丝织品、帛书、帛画、中草药等遗物3000余件。此外，还出土有保存完好的女尸1具，以及方剂书籍帛书《五十二病方》。', 'img/views/mawangdui.jpg', '3', '21');
INSERT INTO `view` VALUES ('28', '杜甫江阁', '杜甫江阁，属于园林仿古建筑，为纪念唐朝诗人杜甫所建。\r\n杜甫江阁位于湖南省长沙市河东城区西湖桥，地处湘江路中段和西湖路交界处，临湘江为湘江风光带的一部分，与橘子洲、岳麓山隔江相望，距天心阁不足一千米。', 'img/views/dufujiangge.jpg', '3', '459');
INSERT INTO `view` VALUES ('29', '白云山', '白云山，位于广东省广州市白云区，为南粤名山之一，自古就有“羊城第一秀”之称。山体相当宽阔，由30多座山峰组成，为广东最高峰九连山的支脉。面积20.98平方公里，主峰摩星岭高382米。 [1] \r\n白云山是广州市风景区行业的第一家国家AAAAA级旅游景区，也是广州市唯一同时拥有全国文明风景旅游区、国家AAAAA级旅游景区两项荣誉的景区。', 'img/views/baiyunshan.jpg', '4', '12');
INSERT INTO `view` VALUES ('30', '长隆旅游度假区', '广州市长隆旅游度假区（Chimelong Tourist Resort），位于广东省广州市番禺区汉溪大道东与长隆地铁大道交汇处，创立于1989年。 [1] \r\n广州长隆旅游度假区是综合性主题旅游度假区，总占地面积1万亩，集旅游景区、酒店餐饮、娱乐休闲于一体， [1]  拥有长隆欢乐世界、长隆国际大马戏、长隆野生动物世界、长隆水上乐园、广州鳄鱼公园、长隆酒店、香江酒店、长隆高尔夫练习中心和香江酒家等设施。 [2] ', 'img/views/changlong.jpg', '4', '23');
INSERT INTO `view` VALUES ('31', '珠江夜游', '珠江包括东江、西江和北江等支流，汇合至广州河段，景色十分秀丽。几十艘豪华游船分别从大沙头码头和天字码头起航 [1]  ，沿途经过海印桥、广州大桥、海心沙、广州塔、猎德大桥返程。全程70分钟左右。', 'img/views/zhujiangyeyou.jpg', '4', '23');
INSERT INTO `view` VALUES ('32', '广州起义烈士陵园', '广州起义烈士陵园，位于广东省广州市越秀区中山二路92号，占地18万平方米，是解放后为纪念1927年12月11日中国共产党领导的广州起义牺牲的烈士于1954年修建的纪念性公园。', 'img/views/gongyuan.jpg', '4', '12');
INSERT INTO `view` VALUES ('33', '长影世纪城', '长影世纪城，位于吉林省长春市南关区，始建于2003年，是集科技、冒险、演艺、观光于一体的综合性旅游区 [1]  。\r\n长影世纪城主要景点由华夏翱翔、空间迷城、精灵王国等多余处景观组成，占地面积100万平方米，是一处特效电影主题公园 [2]  。', 'img/views/changyin.jpg', '5', '12');
INSERT INTO `view` VALUES ('34', '长春奥林匹公园', '长春奥林匹克公园总用地面积52.76万平方米，一期占地面积31.83万平方米。 [1] \r\n长春奥林匹克公园是长春市“十二五”期间规划建设的重大项目之一，是集竞技训练、体育培训、健身娱乐、休闲旅游、商务会展等功能于一体的综合性体育文化活动中心。 [1] ', 'img/views/aolinpike.jpg', '5', '12');
INSERT INTO `view` VALUES ('35', '伪满皇宫博物馆', '伪满皇宫博物院现有包括缉熙楼、勤民楼、同德殿等伪满宫廷原状陈列50个，大型基本陈列2个，专题展览3个，举办临时展览33个，中国国内巡展17个，出国展览11个。伪满皇宫博物院收藏了大批伪满宫廷文物、日本近现代文物、东北近现代文物、民俗文物、近现代有代表性的书画、雕刻、非遗传承人作品等艺术精品。 [1] ', 'img/views/weimanhuanggong.jpg', '5', '35');
INSERT INTO `view` VALUES ('36', '长春莲花山滑雪场', '长春莲花山滑雪场是具有国际标准的、中国一流的、国内距中心城市最近的、集竞技和旅游于一体的、也是长春市最大的综合滑雪场。它位于长春市二道区四家乡青山村，规划占地6平方公里。距长春市区38公里，沿吉长南线驱车30分钟可抵达雪场，距长春国际机场仅20公里，交通极其便捷。试营业一周以来每日接待观光、滑雪游客平均3000余人次。', 'img/views/huaxuechang.jpg', '5', '13');
INSERT INTO `view` VALUES ('37', '鼓浪屿', '鼓浪屿，2007年5月8日起被正式批准为国家5A级旅游景区。被国家地理杂志评选为“中国最美五大城区之首”，素有“海上花园”的美称。岛上完好地保留着许多具有中外建筑风格的建筑物，有“万国建筑博览会”之誉。', 'img/views/gulangyu.jpg', '6', '23');
INSERT INTO `view` VALUES ('38', '厦门大学', '厦门大学被誉为“中国最美大学” [40]  ，校园衔山含湖、面朝大海，背靠万石植物园，一侧是南普陀寺，一侧是白城沙滩，与世界文化遗产鼓浪屿隔海相望。包括群贤楼群、建南楼群、芙蓉楼群以及厦门大学人类学博物馆在内的厦门大学早期建筑为全国重点文物保护单位、入选“首批中国20世纪建筑遗产”名录。 [41] ', 'img/views/xiamendaxue.jpg', '6', '59');
INSERT INTO `view` VALUES ('39', '天竺山森林公园', '天竺山森林公园，位于厦门市海沧区东孚镇，园内有森林植物200多种、野生动物40多种，珍贵野生动物有金钱豹、林麝、果子狸、狐狸、豹猫、锦鸡、青羊、杜鹃等。园内人文景观60余处，其中古迹6处，景点58个。史载，唐十三代皇帝唐宣宗李忱曾在此修度3年。并留下浴龙池、浴龙桥、皇帝井等古迹。', 'img/views/senlingongyuan.jpg', '6', '35');
INSERT INTO `view` VALUES ('40', '海沧野生动物园', '海沧野生动物园，位于海沧台商投资区角嵩路东段，距离海沧大桥8.1公里，占地面积18万平方米，园区依山傍海，错落有致，景色秀丽，是集野生动物散养、驯化、展出和野生动物繁殖、科研于一体的综合性基地；同时也是一处为广大游客提供了解野生动物的科普场所，是福建省的野生动物园。 [53] ', 'img/views/dongwuyuan.jpg', '6', '12');
INSERT INTO `view` VALUES ('41', '岳阳楼', '为国家5A级旅游景区，是长江黄金旅游线上湖南境内的景点，是岳阳旅游业的龙头。岳阳楼耸立在湖南省岳阳市西门城头、洞庭湖畔，自古有“洞庭天下水，岳阳天下楼”之誉，与湖北武汉的黄鹤楼、江西南昌的滕王阁并称为江南三大名楼，并且是三大名楼中一个保持木质架构的古楼。', 'img/views/yueyanglou.jpg', '7', '130');
INSERT INTO `view` VALUES ('42', '君山', '君山岛，古称洞庭山、湘山、有缘山，位于岳阳市境内，是八百里洞庭湖中的一个小岛，与千古名楼岳阳楼遥遥相对，总面积0.96平方公里，由大小七十二座山峰组成，被“道书”列为天下第十一福地，现为国家级重点风景名胜区，国家5A级旅游区。', 'img/views/junshan.jpg', '7', '42');
INSERT INTO `view` VALUES ('43', '屈子祠', '屈子祠，又名屈原庙，为祭祀战国时楚国大夫屈原神位之祠庙，位于湖南省汨罗市，汨罗江畔，玉笥山麓。附近有独醒亭、骚坛、濯缨桥、招屈亭、屈原故居遗址等25处，祠东的屈原碑林嵌有当代书法名家碑刻356块，是世界四大文化名人之一屈原的重要纪念地，也是龙舟竞渡的发源地。', 'img/views/quzisi.jpg', '7', '12');
INSERT INTO `view` VALUES ('44', '团湖荷花公园', '团湖荷花公园位于君山区内，，与东洞庭湖国家自然保护区相邻，省道1804线擦肩而过，占地5平方公里，是岳阳楼——洞庭大桥——君山岛——荷花公园——东洞庭湖——天井山旅游线路的重要组成部分。被上海大世界吉尼斯评为“面积最大的野生荷花成处聚生地”。', 'img/views/hehua.jpg', '7', '13');
INSERT INTO `view` VALUES ('45', '汴河街', '2007中国湖南旅游节开幕式活动场地之一的岳阳楼景区汴河街9月1日试开街。全长300余米，总建筑面积18000平方米的汴河街成为目前国内仿古建筑最逼真，设计功能最全，文化底蕴最厚，沿湖风景最美，面积最大的一条传统风貌商业街，是以岳阳楼文化、洞庭文化、巴陵文化为基础精心打造的一条特色仿古商业街。汴河街建筑设计古朴典雅，是目前国内仿古建筑面积最大的传统风貌商业街，历史上该地区称之为汴河区，仿古街因此而得此名。', 'img/views/bianhejie.jpg', '7', '21');
INSERT INTO `view` VALUES ('46', '海口', '海口，别称“椰城”，海南省省会，国家“一带一路”战略支点城市，中国（海南）自由贸易试验区（港）核心城市，位于北纬19°31′～20°04′，东经110°07′～110°42′之间，地处海南岛北部，东邻文昌，西接澄迈，南毗定安，北濒琼州海峡，是海南省政治、经济、科技、文化中心和最大的交通枢纽。\r\n', 'img/views/haikou.jpeg', '9', '123');
INSERT INTO `view` VALUES ('47', '万宁', '万宁市，隶属于海南省，位于海南岛东南部沿海，东经110.39° 北纬18.8 °，东濒南海，西毗琼中，南邻陵水，北与琼海接壤。万宁市城市总面积为4443.6平方公里，其中陆地面积1883.5平方公里，海域面积2550.1平方公里。', 'img/views/wanning.jpeg', '9', '23');
INSERT INTO `view` VALUES ('48', '文昌', '文昌，古称紫贝，自西汉建置已有2100多年历史，为海南三大历史古邑之一，海南闽南文化发源地，海南文昌航天发射中心所在地，是中国第四座航天之城，处于琼北综合经济区域“一小时经济圈”、“海澄文”一体化综合经济圈 [1]  ，琼北重要的经济、文化、交通、科技、旅游区域中心之一。', 'img/views/wenchang.jpeg', '9', '1238');
INSERT INTO `view` VALUES ('49', '三亚', '三亚，简称崖，古称崖州，别称鹿城。是海南省下辖地级市，位于海南岛的最南端。三亚东邻陵水县，西接乐东县，北毗保亭县，南临南海，介于北纬18°09′34″~18°37′27″、东经108°56′30″~109°48′28″之间。 [1]  三亚市陆地总面积1919.58平方千米，海域总面积6000平方千米。东西长91.6千米，南北宽51公里，下辖四个区。 [2]  2018年，全市年末户籍人口614647人，聚居了汉族、黎族、苗族等20多个民族。 [3] ', 'img/views/sanya.jpeg', '9', '432');
INSERT INTO `view` VALUES ('50', '西双版纳', '西双版纳傣族自治州，是云南省的8个自治州之一，首府景洪市。位于云南省最南端，地处北纬约21°10’，东经99°55’至101°50’之间，属北回归线以南的热带湿润区，东西面与江城县、普洱市相连；西北面与澜沧县为邻，东南部、南部和西南部分别与老挝、缅甸山水相连，邻近泰国和越南，边界线长达966.3公里，约等于云南省边境线总长的1/4，面积19582.45平方公里。 [1] ', 'img/views/xishuangbanna.jpeg', '8', '145');
INSERT INTO `view` VALUES ('51', '大理', '大理白族自治州地处云南省中部偏西，海拔2090米，东邻楚雄州，南靠普洱市、临沧市，西与保山市、怒江州相连，北接丽江市。地跨东经98°52′～101°03′，北纬24°41′～26°42′之间，东巡洱海，西及点苍山脉，辖大理市和祥云、弥渡、宾川、永平、云龙、洱源、鹤庆、剑川8个县以及漾濞、巍山、南涧3个少数民族自治县，是中国西南边疆开发较早的地区之一。', 'img/views/dali.jpeg', '8', '654');
INSERT INTO `view` VALUES ('52', '丽江', '丽江，云南省地级市，位于云南省西北部，滇川藏三省区交界处，是国际知名旅游城市 [1]  、古代“南方丝绸之路”和“茶马古道”的重要通道 [2]  。市区中心位于东经100°25\'，北纬26°86\'，总面积2.06万平方公里，北连迪庆藏族自治州，南接大理白族自治州，西邻怒江傈僳族自治州，东与四川凉山彝族自治州和攀枝花市接壤，距昆明市527公里。 [3] ', 'img/views/lijiang.jpeg', '8', '4348');
INSERT INTO `view` VALUES ('53', '自然公园', '森林公园以大面积人工林或天然林为主体而建设的公园。天然公园保管有自然景观。森林公园除保护森林景色自然特征外，并根据造园要求适当加以整顿布置。', 'img/views/senlin.jpeg', '8', '23');
INSERT INTO `view` VALUES ('54', '华清宫', '华清宫背山面渭，倚骊峰山势而筑，规模宏大，建筑壮丽，楼台馆殿，遍布骊山上下。初名“汤泉宫”，后改名温泉宫。唐玄宗更华清宫，因在骊山，又叫骊山宫，亦称骊宫、绣岭宫。华清宫始建于唐初，鼎盛于唐玄宗执政以后。唐玄宗悉心经营建起如此宏大的离宫，他几乎每年十月都要到此游幸。岁尽始还长安。安史乱后，政局突变，华清宫的游幸迅速衰落，唐朝以后各代皇帝已很少出游华清宫。后历代皇家有维修，到解放前已是汤池寥落，宫殿萧疏。华清池，解放后人民政府自1959年起进行了大规模的扩建。', 'img/views/huaqinggong.jpeg', '10', '123');
INSERT INTO `view` VALUES ('55', '兵马俑', '兵马俑，即秦始皇兵马俑，亦简称秦兵马俑或秦俑，第一批全国重点文物保护单位，第一批中国世界遗产，位于今陕西省西安市临潼区秦始皇陵以东1.5千米处的兵马俑坑内。 [1] ', 'img/views/bingmayong.jpeg', '10', '124');
INSERT INTO `view` VALUES ('56', '鼓楼', '西安鼓楼，位于古都西安市中心，明城墙内东西南北四条大街交汇处的西安钟楼西北方约200米处。建于明太祖朱元璋洪武十三年（1380年），是中国古代遗留下来众多鼓楼中形制最大、保存最完整鼓楼之一。', 'img/views/xiangulou.jpeg', '10', '457');
INSERT INTO `view` VALUES ('57', '西安城墙', '西安城墙又称西安明城墙，是中国现存规模最大、保存最完整的古代城垣 [1-7]  ，是第一批全国重点文物保护单位、国家AAAAA级旅游景区 [8-9]  [10]  。广义的西安城墙包括西安唐城墙和西安明城墙，但一般特指狭义上的西安明城墙。 [11] \r\n西安城墙又称西安明城墙，是中国现存规模最大、保存最完整的古代城垣 [1-7]  ，是第一批全国重点文物保护单位、国家AAAAA级旅游景区 [8-9]  [10]  。广义的西安城墙包括西安唐城墙和西安明城墙，但一般特指狭义上的西安明城墙。 [11] \r\n', 'img/views/xianchengqiang.jpeg', '10', '343');
