  `tour_Price` double(5,2) DEFAULT NULL COMMENT '产品价格 ',
  `tour_Desc` varchar(500) DEFAULT NULL COMMENT '	产品描述',
  `tour_Status` int(11) DEFAULT NULL COMMENT '状态 (0关闭 1开启)',
