DROP TABLE IF EXISTS `travellog`;
CREATE TABLE `travellog` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `TL_Title` varchar(100) DEFAULT NULL COMMENT '游记标题',
  `TL_Summary` varchar(200) DEFAULT NULL COMMENT '游记摘要//截取内容前多少个字',
  `TL_release_Date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '发布日期',
  `TL_Content` text COMMENT '游记内容',
  `TL_key_Word` varchar(200) DEFAULT NULL COMMENT '关键字',
  `TL_Click_Count` int(11) DEFAULT '0' COMMENT '点击数(浏览量)',
  `TL_Like_Count` int(11) DEFAULT '0' COMMENT '点赞数',
  `TL_Favorite_Count` int(11) DEFAULT '0' COMMENT '收藏数',
  `UserInfo_Id` int(11) DEFAULT NULL COMMENT '发布用户id(外键)',
  `TL_Status` int(1) DEFAULT '1' COMMENT '--状态(0可读,1不可读)',
  `TL_photo` varchar(50) DEFAULT NULL,
  `TL_BG_photo` varchar(50) DEFAULT NULL,
  `TL_city_name` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

